import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled, { keyframes } from "styled-components"
import { ally } from "../../utils/data/dataSet";
import { rangeCheck } from "../../utils/functions/isRange";
import { fieldUnit } from "../../store/slices/fieldUnit";

const moveAlly = keyframes`
    0%{left: 400px;}
    100%{left: 2000px;}
`;
const moveEnemy = keyframes`
    0%{left: 1700px;}
    100%{left: 100px;}
`;
const Container = styled.div`
    display: flex;
    position: absolute;
    justify-content: center;
    align-content: end;
    flex-wrap: wrap;
    left: 400px;
    bottom: 50px;
    width: 100px;
    height: 130px;
    animation: ${props => props.team === ally ? moveAlly : moveEnemy} 30s 0s 1 linear alternate;
    animation-play-state: ${(props) => props.animation === "걷기" ? "running" : "paused"};
    transform: ${(props) => props.team === ally ? "scaleX(1)" : "scaleX(-1)"};
`;
const UpperBody = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const LowerBody = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const HPBox = styled.div`
    position: relative;
    top: -20px;
    left: 0;
    width: 50px;
    height: 7px;
    background-image: ${(props) => "linear-gradient(to left, red " + props.hp +  "%, transparent 0%);"};
    display: none;
`;
const HitBox = styled.div`
    width: 45%;
    height: calc(100% - 30px);
    z-index: 0;
    &:hover {
        ${HPBox} {
            display: block;
        }
    }
`;
const Body = styled.div`
    position: absolute;
    width: 100%;
    height: 100%;
`;

export default function Unit({id, team}){

    const fieldUnits = useSelector(state => state.fieldUnit)
    const myUnit = fieldUnits.filter(e => e.id === id)[0]
    const myUnitIndex = fieldUnits.findIndex(e => e.id === id)
    const dispatch = useDispatch();

    useEffect(() => {

        if(rangeCheck(fieldUnits, myUnit, team, myUnit.hitRange) > -1 && rangeCheck(fieldUnits, myUnit, team, myUnit.shortRange) < myUnitIndex){
            dispatch(fieldUnit.actions.setLowerBody({motion:"대기", index:myUnitIndex}))
        }else if(rangeCheck(fieldUnits, myUnit, Number(!team), myUnit.shortRange) > -1){
            dispatch(fieldUnit.actions.setLowerBody({motion:"근접", index:myUnitIndex}))
        }else{
            dispatch(fieldUnit.actions.setLowerBody({motion:"걷기", index:myUnitIndex}))
        }

        if(rangeCheck(fieldUnits, myUnit, Number(!team), myUnit.shortRange) > -1){
            dispatch(fieldUnit.actions.setUpperBody({motion:"근접", index:myUnitIndex}))
        }else if(rangeCheck(fieldUnits, myUnit, Number(!team), myUnit.longRange) > -1){
            dispatch(fieldUnit.actions.setUpperBody({motion:"원거리", index:myUnitIndex}))
        }else if(rangeCheck(fieldUnits, myUnit, team, myUnit.hitRange) > -1 && rangeCheck(fieldUnits, myUnit, team, myUnit.shortRange) < myUnitIndex){
            dispatch(fieldUnit.actions.setUpperBody({motion:"대기", index:myUnitIndex}))
        }else{
            dispatch(fieldUnit.actions.setUpperBody({motion:"걷기", index:myUnitIndex}))
        }

    }, [myUnit])

    useEffect(() => {

        // 만약 공격이면 공격속도에 맞게 interval을 생성해서 공격하기

    }, [])

    // 위의 기능들을 재활용 하기 위한 함수 만들기

    return (
        <Container id={id} team={team} animation={myUnit.lowerBody}>
            <Body>
                <UpperBody src={require("../../assets/images/units/level" + myUnit.level + "/" + myUnit.lowerBody + "하체.gif")}></UpperBody>
                <LowerBody src={require("../../assets/images/units/level" + myUnit.level + "/" + myUnit.upperBody + "상체.gif")}></LowerBody>
                {/* <audio ref={atteckSound} src={"../sound/" + unitInfo.name + "/" + unitInfo.body + ".mp3"}></audio> */}
            </Body>
            <HitBox><HPBox hp={(myUnit.hp / myUnit.maxHp) * 100}/></HitBox>
        </Container>
    )
}
