import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled, { keyframes } from "styled-components"

const moveAlly = keyframes`
    0%{left: 0;}
    100%{left: 700px;}
`;
const moveEnemy = keyframes`
    0%{left: 600px;}
    100%{left: 140px;}
`;
const Container = styled.div`
background-color: red;
    display: flex;
    position: absolute;
    justify-content: center;
    align-content: end;
    flex-wrap: wrap;
    left: 300px;
    bottom: 50px;
    width: 100px;
    height: 130px;
    /* animation: ${props => props.className === "ally" ? moveAlly : moveEnemy} 20s 0s infinite linear alternate;
    animation-play-state: ${(props) => props.animation || "paused"}; */

`;
const Body = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const Leg = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const HPBox = styled.div`
    position: relative;
    top: -20px;
    left: 0;
    width: 50px;
    height: 7px;
    background-image: ${(props) => {
            return "linear-gradient(to left, red " + 100 +  "%, transparent 0%);"
        }
    };
    display: none;
`;
const HitBox = styled.div`
    width: 45%;
    height: calc(100% - 30px);
    z-index: 0;
    &:hover {
        ${HPBox} {
            display: block;
        }
    }
`;
const UnitBody = styled.div`
    position: absolute;
    width: 100%;
    height: 100%;
`;

// transform: ${(props) => {
//     if (props.className === "enemy") {
//         return "scaleX(-1)"
//     }else{
//         return "scaleX(1)"
//     }
// }};
export default function Unit(){

    return (
        <Container>
            <UnitBody>
                {/* <Leg src={"../image/unit/" + unitInfo.era + "시대/" + unitInfo.name + "/" + unitInfo.leg + "하체.gif"}></Leg>
                <Body src={"../image/unit/" + unitInfo.era + "시대/" + unitInfo.name + "/" + unitInfo.body + "상체.gif"}></Body>
                <audio ref={atteckSound} src={"../sound/" + unitInfo.name + "/" + unitInfo.body + ".mp3"}></audio> */}
            </UnitBody>
            <HitBox><HPBox/></HitBox>
        </Container>
    )
}
