import { useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled, { keyframes } from "styled-components"
import { ally } from "../../utils/data/dataSet";
import { rangeCheck } from "../../utils/functions/isRange";
import { fieldUnit } from "../../store/slices/fieldUnit";

const moveAlly = keyframes`
    0%{left: 400px;}
    100%{left: 2000px;}
`;
const moveEnemy = keyframes`
    0%{left: 1700px;}
    100%{left: 100px;}
`;
const Container = styled.div`
    display: flex;
    position: absolute;
    justify-content: center;
    align-content: end;
    flex-wrap: wrap;
    left: 400px;
    bottom: 50px;
    width: 100px;
    height: 130px;
    animation: ${props => props.team === ally ? moveAlly : moveEnemy} 30s 0s 1 linear alternate;
    animation-play-state: ${(props) => props.animation === "걷기" ? "running" : "paused"};
    transform: ${(props) => props.team === ally ? "scaleX(1)" : "scaleX(-1)"};
`;
const UpperBody = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const LowerBody = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const HPBox = styled.div`
    position: relative;
    top: -20px;
    left: 0;
    width: 50px;
    height: 7px;
    background-image: ${(props) => "linear-gradient(to left, red " + props.hp +  "%, transparent 0%);"};
    display: none;
`;
const HitBox = styled.div`
    width: 45%;
    height: calc(100% - 30px);
    z-index: 0;
    &:hover {
        ${HPBox} {
            display: block;
        }
    }
`;
const Body = styled.div`
    position: absolute;
    width: 100%;
    height: 100%;
`;

export default function Unit({id, team}){

    const fieldUnits = useSelector(state => state.fieldUnit)
    const myUnit = fieldUnits.filter(e => e.id === id)[0]
    const myUnitIndex = fieldUnits.findIndex(e => e.id === id)
    const attackSound = useRef(null);
    const dispatch = useDispatch();

    useEffect(() => {
        console.log(fieldUnits);
        if(myUnit.hp < 0){
            dispatch(fieldUnit.actions.setLowerBody({motion:"죽기", id:id}))
            dispatch(fieldUnit.actions.setUpperBody({motion:"죽기", id:id}))

            setTimeout(() => {
                dispatch(fieldUnit.actions.removeUnit({id:id}))
            }, 1000);
        }else{
            if(rangeCheck(fieldUnits, myUnit, team, myUnit.hitRange) > -1 && rangeCheck(fieldUnits, myUnit, team, myUnit.shortRange) < myUnitIndex){
                dispatch(fieldUnit.actions.setLowerBody({motion:"대기", id:id}))
            }else if(rangeCheck(fieldUnits, myUnit, Number(!team), myUnit.shortRange) > -1){
                dispatch(fieldUnit.actions.setLowerBody({motion:"근접", id:id}))
            }else{
                dispatch(fieldUnit.actions.setLowerBody({motion:"걷기", id:id}))
            }

            if(rangeCheck(fieldUnits, myUnit, Number(!team), myUnit.shortRange) > -1){
                dispatch(fieldUnit.actions.setUpperBody({motion:"근접", id:id}))
            }else if(rangeCheck(fieldUnits, myUnit, Number(!team), myUnit.longRange) > -1){
                dispatch(fieldUnit.actions.setUpperBody({motion:"원거리", id:id}))
            }else if(rangeCheck(fieldUnits, myUnit, team, myUnit.hitRange) > -1 && rangeCheck(fieldUnits, myUnit, team, myUnit.shortRange) < myUnitIndex){
                dispatch(fieldUnit.actions.setUpperBody({motion:"대기", id:id}))
            }else{
                dispatch(fieldUnit.actions.setUpperBody({motion:"걷기", id:id}))
            }
        }

    }, [myUnit])

    useEffect(() => {

        let interval = null

        if(myUnit.upperBody === "원거리" || myUnit.upperBody === "근접"){

            interval = setInterval(() => {
                // 이부분 수정 필요!
                const index = rangeCheck(fieldUnits, myUnit, Number(!team), myUnit.upperBody === "원거리" ? myUnit.longRange : myUnit.shortRange)
                if(index > -1) dispatch(fieldUnit.actions.hitUnit({id:fieldUnits[index].id, damage:myUnit.damage}))
                // ---------------------------------
                try {
                    attackSound.current.play()
                } catch (error) {
                    console.log(error);
                }
            }, myUnit.upperBody === "원거리" ? myUnit.attackLongSpeed : myUnit.attackShortSpeed);
        }

        return () => clearInterval(interval)

    }, [myUnit.upperBody])

    return (
        <Container id={id} team={team} animation={myUnit.lowerBody}>
            <Body>
                <UpperBody src={require("../../assets/images/units/level" + myUnit.level + "/" + myUnit.lowerBody + "하체.gif")}></UpperBody>
                <LowerBody src={require("../../assets/images/units/level" + myUnit.level + "/" + myUnit.upperBody + "상체.gif")}></LowerBody>
                <audio ref={attackSound} src={require("../../assets/sounds/units/level" + myUnit.level + "/unit" + myUnit.unit + "/" + "근접.mp3")} ></audio>
            </Body>
            <HitBox><HPBox hp={(myUnit.hp / myUnit.maxHp) * 100}/></HitBox>
        </Container>
    )
}
