import { useDispatch, useSelector } from "react-redux";
import { styled } from "styled-components";
import targetImg from "../../assets/images/turrets//target.png"
import { selectedTower } from "../../store/slices/selectedTurret";
import { status } from "../../store/slices/status";
import { ally } from "../../utils/data/dataSet";
import { useEffect } from "react";

const Container = styled.div`
    display: flex;
    position: absolute;
    align-items: center;
    justify-content: center;
    width: 55px;
    height: 55px;
    left: ${props => props.location.left};
    bottom: ${props => props.location.bottom};
    z-index: 3;
`;
const TurretImage = styled.img`
    position: absolute;
    width: 140px;
    height: 140px;
    transform: rotate( ${props => props.deg + "deg"});
`;
const TargetImage = styled.img`
    position: absolute;
    width: 55px;
    height: 55px;
    z-index: 10;
    &:hover{
        background-color: rgba(240, 240, 240, 0.5);
    }
    display: ${props =>{
        if(props.menu === "add"){
            if(props.towerInfo === null)
                return "block"
            else
                return "none"
        }else if(props.menu === "cell"){
            if(props.towerInfo !== null)
                return "block"
            else
                return "none"
        }else{
            return "none"
        }
    }};
`;

export default function Turret({location, menu, team, turretInfo, index}){

    const dispatch = useDispatch();
    const selectedTurret = useSelector(state => state.selectedTurret)

    // status의 menu가 add인지 cell인지에 따라서 TargetImage의 컴포넌트 포함 여부를 결정한다.
    // menu에 따라 TargetImage의 액션을 다르게 준다.
    // add일 경우 status의 tower에 해당 터렛의 정보를 입력한다.
    // 이때 해당 터렛의 정보는 ShopTurret에서 클릭한 터렛의 정보로 한다.
    // cell일 경우 클릭한 Index의 tower의 정보를 터렛의 금액의 절반을 회수하고, null로 한다.

    // 터렛의 범위에 FieldUnits의 상대 유닛이 들어오면 공격모션으로 변환 한다.
    // 공격 속도에 맞게 투사체를 발사한다.
    // 투사체는 상대 유닛에 적중하면 상대 유닛의 체력을 깎은 후 소멸한다.
    return (
        <Container location={location}>
            {/* {turretInfo !== null ? <TurretImage src={require("../../assets/images/turrets/level" + turretInfo.level + "/turret" + turretInfo.turret + ".gif")}></TurretImage> : <div></div>} */}
            <TargetImage src={targetImg} menu={menu} towerInfo={turretInfo} onClick={e => {
                if(menu === "add"){
                    dispatch(status.actions.setTurret({index:index, turret:selectedTurret, team:team}))
                }else{
                    dispatch(status.actions.removeTurret({index:index}))
                }
            }}></TargetImage>
        </Container>
    )
}
