import { createSlice } from "@reduxjs/toolkit";
import { ally, enemy, nexusHp } from "../../utils/data/dataSet"

export const fieldUnit = createSlice({
    name:"fieldUnit",
    initialState:[{
        id:"ally",
        hp:500,
        team:ally,
        location:null,
        range:100,
        hitRange:150
    },{
        id:"enemy",
        hp:500,
        team:enemy,
        location:null,
        range:100,
        hitRange:150
    }],
    reducers:{
        addUnit:(state, action) => {
            state.push(action.payload.unit)
        },
        removeUnit:(state, action) => {
            state.splice(action.payload.index, 1)
        },
        hitUnit:(state, action) => {
            const payload = action.payload
            state[payload.index].hp -= payload.damage
        },
        updateLocation:(state, action) => {
            const index = state.findIndex(e =>  e.id.toString() === action.payload.id.toString())
            state[index].location = action.payload.location
        },
        setUpperBody:(state, action) => {
            const payload = action.payload
            state[payload.index].upperBody = payload.motion
        },
        setLowerBody:(state, action) => {
            const payload = action.payload
            state[payload.index].lowerBody = payload.motion
        }
    }
})
