import { useSelector } from "react-redux";
import { styled } from "styled-components";
import targetImg from "../../assets/images/towers/target.png"

const Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    width: 55px;
    height: 55px;
`;
const TowerImage = styled.img`
    position: absolute;
    width: 80px;
    height: 80px;
`;
const TurretImage = styled.img`
    position: absolute;
    width: 140px;
    height: 140px;
    transform: rotate( ${props => props.deg + "deg"});
`;
const TargetImage = styled.img`
    position: absolute;
    width: 55px;
    height: 55px;
    z-index: 10;
    &:hover{
        background-color: rgba(240, 240, 240, 0.5);
    }
`;

export default function Turret({team, index}){

    const status = useSelector(state => state.status[team])

    return (
        <Container>
            <TowerImage src={require("../../assets/images/towers/level" + status.level + ".png")}></TowerImage>
            {
                status.tower[index] !== null ? <TurretImage src={"require()"}></TurretImage> : null
            }
            <TargetImage src={targetImg}></TargetImage>
        </Container>
    )
}
