import { createSlice } from "@reduxjs/toolkit";
import { ally, enemy, nexusHp } from "../../utils/data/dataSet"
import getKey from "../../utils/functions/getKey";

export const fieldUnit = createSlice({
    name:"fieldUnit",
    initialState:[{
        id:"ally",
        hp:500,
        team:ally,
        location:null
    },{
        id:"enemy",
        hp:500,
        team:enemy,
        location:null
    }],
    reducers:{
        addUnit:(state, action) => {
            state.push(action.payload.unit)
        },
        removeUnit:(state, action) => {

        },
        hitUnit:(state, action) => {

        },
        updateLocation:(state, action) => {
            const index = state.findIndex(e => {
                console.log(e.id + " " + action.payload.id.toString());
                return e.id === action.payload.id.toString()
            })
            state[index].location = action.payload.location
        }
    }
})
