import { useSelector } from "react-redux";
import { styled } from "styled-components";
import Tower from "../Tower";

const Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    width: 55px;
    height: 55px;
`;
const TowerImage = styled.img`
    position: absolute;
    height: 80px;
`;
const TurretImage = styled.img`
    position: absolute;
    width: 140px;
    height: 140px;
    transform: rotate( ${props => props.deg + "deg"});
`;
const TargetImage = styled.img`
    position: absolute;
    width: 55px;
    height: 55px;
    z-index: 10;
    &:hover{
        background-color: rgba(240, 240, 240, 0.5);
    }
`;

export default function Floor({team, index}){

    const status = useSelector(state => state.status[team])

    return (
        <Container>
            <TowerImage src={require("../../../assets/images/towers/level" + status.level + ".png")}></TowerImage>
            {
                status.tower[index] !== null ? <Tower></Tower> : null
            }
        </Container>
    )
}
