import { styled } from "styled-components"
import {createSlice} from "@reduxjs/toolkit"
import { useDispatch, useSelector } from "react-redux";

export const unit = createSlice({
    name:"unit",
    initialState:[{
        id:Number(Date.now()),
        name:"base",
        team:"ally",
        maxHp:500,
        hp:400,
    },{
        id:Number(Date.now() + 10),
        name:"base",
        team:"enemy",
        maxHp:500,
        hp:500,
    }],
    reducers:{
        hit:(state, action)=>{
            const index = state.findIndex(obj => {
                return Number(obj.id) === Number(action.index)
            })
            if(index > -1) state[index].hp = state[index].hp - action.damage
        },
        removeUnit:(state, action)=>{
            const index = state.findIndex(obj => {
                return Number(obj.id) === Number(action.id)
            })
            state.splice(index, 1)
        },
        addUnit:(state, action)=>{
            state.push(action.data)
        },
        setIndex:(state, action)=>{
            state[action.index].index = action.index
        },
        setLegMotion:(state, action)=> {
            state[state.findIndex(e => Number(e.id) === Number(action.id))].leg = action.motion
        },
        setBodyMotion:(state, action)=> {
            state[state.findIndex(e => Number(e.id) === Number(action.id))].body = action.motion
        },
        setDeadMotion:(state, action)=> {
            state[state.findIndex(e => Number(e.id) === Number(action.id))].leg = action.motion
        }
    }
})
export const attacks = createSlice({
    name:"attacks",
    initialState:[],
    reducers:{
        addAttack:(state, action) => {
            state.push(action.data)
        },
        removeAttack:(state, action) => {
            const index = state.findIndex(e => e.id === action.index)
            state.splice(index, 1)
        }
    }
})

