import { useSelector } from "react-redux";
import { styled } from "styled-components"
import Turret from "../turret/Turret";

const Container = styled.div`
    position: absolute;
    display: flex;
    width: 100px;
    height: 100px;
    left: 120px;
    bottom: 210px;
`;

export default function Tower({team}){

    const towers = useSelector(state => state.status[team].tower)

    return (
        <Container>d
            {
                towers.map((e, k) => {
                    <Turret team={team} key={k} index={k}></Turret>
                })
            }
        </Container>
    )
}
