import { createSlice } from "@reduxjs/toolkit";
import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { keyframes, styled } from "styled-components"

export const load = createSlice({
    name:"load",
    initialState:[],
    reducers:{
        removeFirst:(state) => {
            state.shift()
        },
        addUnit:(state, action) => {
            state.push(action.data)
        }
    }
})
const loading = keyframes`
    0%{width:0%}
    100%{width:100%}
`;

const Load = styled.div`
    margin-top: 2px;
    display: flex;
    flex-wrap: wrap;
    align-content: start;
    width: 43%;
    height: 100%;
    gap: 1%;
    z-index: 1;
`;
const LoadingBar = styled.div`
    border: 2.5px solid black;
    width: 70%;
    height: 8%;
`;
const Progress = styled.div`
    width: 0%;
    height: 100%;
    background-color: lightgray;
    animation: ${loading} ${props => (props.load.length > 0 ? props.load[0].createTime : 0) + "s"} 0s 1;
`;
const UnitInfo = styled.div`
    color: yellow;
    font-size: 30px;
`;
const LoadWait = styled.div`
    border: 2.5px solid black;
    width: 3.5%;
    height: 9%;
    background-color: ${props => {
        return props.load.length - 1 >= props.index ? "lightgray" : "none"
    }};
`;

export default function Loading(){

    const [key, setKey] = useState(0);
    const dispatch = useDispatch()
    const loadInfo = useSelector(state => {
        return state.load
    })

    useEffect(() => {

        if(loadInfo.length === 1) setKey(prev => prev + 1)

    }, [loadInfo])

    return(
        <Load>
            <LoadingBar>
                <Progress key={key} load={loadInfo} onAnimationEnd={e => {
                    if(loadInfo.length > 0){
                        dispatch({type:"unit/addUnit", data:loadInfo[0]})
                        dispatch({type:"load/removeFirst"})
                        setKey(prev => prev + 1)
                    }
                }}></Progress>
            </LoadingBar>
            <LoadWait load={loadInfo} index={0} ></LoadWait>
            <LoadWait load={loadInfo} index={1}></LoadWait>
            <LoadWait load={loadInfo} index={2}></LoadWait>
            <LoadWait load={loadInfo} index={3}></LoadWait>
            <LoadWait load={loadInfo} index={4}></LoadWait>
            <UnitInfo>Club Man...</UnitInfo>
        </Load>
    )
}
