import { useSelector } from "react-redux";
import { styled } from "styled-components";

const TowerImage = styled.img`
    position: absolute;
    left: 0;
    bottom: 0;
    width: 130%;
`;

export default function Tower({team, index}){

    const status = useSelector(state => state.status[team])

    return (
            <TowerImage src={require("../../../assets/images/towers/level" + status.level + "/" + (Number(index) + 1) + ".png")}></TowerImage>
    )
}
