import { useSelector } from "react-redux";
import { styled } from "styled-components";
import targetImg from "../../assets/images/turrets//target.png"

const Container = styled.div`
    display: flex;
    position: absolute;
    align-items: center;
    justify-content: center;
    width: 55px;
    height: 55px;
    left: ${props => props.location.left};
    bottom: ${props => props.location.bottom};
    z-index: 3;
`;
const TurretImage = styled.img`
    position: absolute;
    width: 140px;
    height: 140px;
    transform: rotate( ${props => props.deg + "deg"});
`;
const TargetImage = styled.img`
    position: absolute;
    width: 55px;
    height: 55px;
    z-index: 10;
    &:hover{
        background-color: rgba(240, 240, 240, 0.5);
    }
`;

export default function Turret({location}){

    // status의 menu가 add인지 cell인지에 따라서 TargetImage의 컴포넌트 포함 여부를 결정한다.
    // menu에 따라 TargetImage의 액션을 다르게 준다.
    // add일 경우 status의 tower에 해당 터렛의 정보를 입력한다.
    // 이때 해당 터렛의 정보는 ShopTurret에서 클릭한 터렛의 정보로 한다.
    // cell일 경우 클릭한 Index의 tower의 정보를 터렛의 금액의 절반을 회수하고, null로 한다.

    return (
        <Container location={location}>
            <TurretImage ></TurretImage>
            <TargetImage src={targetImg}></TargetImage>
        </Container>
    )
}
