import { createSlice } from "@reduxjs/toolkit";

export const selectedTurret = createSlice({
    name:"selectedTurret",
    initialState:{},
    reducers:{
        setSelectedTurret:(state, action) => {
            state = action.payload.turret
        }
    }
})
