export function isRange(range, myUnit, otherUnit){

    const myLocation = myUnit.location

    const leftRange = myLocation.x - range
    const rightRange = myUnit.width + myLocation.x + range;

    return leftRange < otherUnit.location.left && rightRange > otherUnit.location.right
}

export function rangeCheck(fieldUnitList, myUnit, team, range){

    const rangedIndex = fieldUnitList.findIndex(e => e.team === team && e.id !== myUnit.id && myUnit.location !== null && isRange(range, myUnit, e))

    return rangedIndex === -1 ? -1 : rangedIndex
}
