import { useDispatch, useSelector } from "react-redux";
import { styled } from "styled-components";
import targetImg from "../../assets/images/turrets//target.png"
import { selectedTower } from "../../store/slices/selectedTurret";
import { status } from "../../store/slices/status";
import { ally } from "../../utils/data/dataSet";
import { useEffect, useRef } from "react";
import { rangeCheck } from "../../utils/functions/isRange";

const Container = styled.div`
    display: flex;
    position: absolute;
    align-items: center;
    justify-content: center;
    width: 55px;
    height: 55px;
    left: ${props => props.location.left};
    bottom: ${props => props.location.bottom};
    z-index: 3;
`;
const TurretImage = styled.img`
    position: absolute;
    width: 140px;
    height: 140px;
    transform: rotate( ${props => props.deg + "deg"});
`;
const TargetImage = styled.img`
    position: absolute;
    width: 55px;
    height: 55px;
    z-index: 10;
    &:hover{
        background-color: rgba(240, 240, 240, 0.5);
    }
    display: ${props =>{
        if(props.menu === "add"){
            if(props.towerInfo === null)
                return "block"
            else
                return "none"
        }else if(props.menu === "cell"){
            if(props.towerInfo !== null)
                return "block"
            else
                return "none"
        }else{
            return "none"
        }
    }};
`;

export default function Turret({location, menu, team, turretInfo, index}){

    const dispatch = useDispatch();
    const selectedTurret = useSelector(state => state.selectedTurret)
    const fieldUnits = useSelector(state => state.fieldUnit)
    const turretRef = useRef(null);

    useEffect(() => {

        if(turretInfo !== null){
            const location = {
                x:turretRef.current.getBoundingClientRect().x,
                y:turretRef.current.getBoundingClientRect().y,
                left:turretRef.current.getBoundingClientRect().left,
                right:turretRef.current.getBoundingClientRect().right,
                top:turretRef.current.getBoundingClientRect().top,
                bottom:turretRef.current.getBoundingClientRect().bottom,
                centerX:turretRef.current.getBoundingClientRect().x + turretRef.current.offsetWidth / 2,
            }
            if(rangeCheck(fieldUnits, {id:null, location:location}, Number(team), turretInfo.range)){
                console.log("들어옴");
            }else{
                console.log("안 들어옴");
            }
        }

    }, [fieldUnits])
    // 터렛의 범위에 FieldUnits의 상대 유닛이 들어오면 공격모션으로 변환 한다.
    // 공격 속도에 맞게 투사체를 발사한다.
    // 투사체는 상대 유닛에 적중하면 상대 유닛의 체력을 깎은 후 소멸한다.

    return (
        <Container ref={turretRef} location={location}>
            {turretInfo !== null && turretInfo !== undefined ? <TurretImage src={require("../../assets/images/turrets/level" + turretInfo.level + "/turret" + turretInfo.turret + "/" + turretInfo.attackMotion + ".gif")}></TurretImage> : <div></div>}
            <TargetImage src={targetImg} menu={menu} towerInfo={turretInfo} onClick={e => {
                if(menu === "add") {
                    dispatch(status.actions.setTurret({index:index, turret:selectedTurret, team:team}))
                    dispatch(status.actions.setMenu({menu:"main"}))
                } else {
                    dispatch(status.actions.removeTurret({index:index, team:team}))
                    dispatch(status.actions.setMenu({menu:"main"}))
                }
            }}></TargetImage>
        </Container>
    )
}
