export function isRange(range, myUnit, otherUnit){

    const myLocation = myUnit.location

    const leftRange = myLocation.x - range
    const rightRange = myUnit.width + myLocation.x + range;

    return leftRange < otherUnit.location.centerX && rightRange > otherUnit.location.centerX
}

export function rangeCheck(fieldUnitList, myUnit, team){

    const rangedIndex = fieldUnitList.findIndex(e => isRange(50, myUnit, e) && e.team === team)

    return rangedIndex === -1 ? -1 : rangedIndex
}
