import { useDispatch, useSelector } from "react-redux";
import { keyframes, styled } from "styled-components";
import { attacks } from "../../store/slices/attacks";
import { useEffect, useState } from "react";
import { rangeCheck } from "../../utils/functions/isRange";

const animation = (start, finish) => keyframes`
    0%{
        left: ${start.left + "px"};
        top: ${start.top + "px"};
    }
    100%{
        left: ${finish.left + "px"};
        top: ${finish.top + "px"};
    }
`;

const Container = styled.div`
    position: absolute;
    width: 50px;
    height: 50px;
    left: 1000px;
    bottom: 0;
    background-color: red;
    animation-name: ${props => animation(props.start, props.finish)};
    animation-duration: 2s;
    animation-iteration-count: 1;
`;

export default function Attack({id, team}){

    const dispatch = useDispatch();
    const attack = useSelector(state => state.attacks[state.attacks.findIndex(e => e.id === id)])
    const target = useSelector(state => state.fieldUnit[state.fieldUnit.findIndex(e => e.id === attack.target)])
    const targetLocation = useState(target.location)
    const fieldUnits = useSelector(state => state.fieldUnit)
    useEffect(() => {
        // if(rangeCheck(fieldUnits, attack, Number(!team), 10)) {
        //     console.log("적중!");
        // }else{
        //     console.log("빗나감!");
        // }
    }, [])
    return (
        <>
          {
          target !== null ? <Container
              start={{ left: attack.location.left, top: attack.location.top }}
              finish={{ left: target.location.left, top: target.location.top }}
              onAnimationEnd={e => dispatch(attacks.actions.removeAttack({ id: id }))}
            >{target.location.left + " " + attack.location.left}</Container> : <></>}
        </>
      );
}
