import { useDispatch, useSelector } from "react-redux";
import { styled } from "styled-components";

const Container = styled.div`
    display: flex;
    position: absolute;
    align-items: end;
    flex-direction: column-reverse;
    width: 300px;
    height: 530px;
    bottom: 0;
    left: ${(props) => {
        if (props.className === "enemy") {
            return "700px"
        }else{
            return "-170px"
        }
    }};
`;
const HpState = styled.div`
    position: absolute;
    top: 0px;
    left: ${props => {
        if(props.className === "ally") return "70px";
    }};
    right: ${props => {
        if(props.className === "enemy") return "70px";
    }};
    width: 65px;
    height: 280px;
    gap: 10px;
`;
const HpNumber = styled.div`
    position: absolute;
    left: ${props => {
        if(props.className === "enemy") return "0"
    }};
    right: ${props => {
        if(props.className === "ally") return "0"
    }};
    font-size: 20px;
    color: red;
    text-align: center;
`;
const HpBar = styled.div`
    position: absolute;
    left: ${props => {
        if(props.className === "ally") return "0"
    }};
    right: ${props => {
        if(props.className === "enemy") return "0"
    }};
    background-image: ${(props) => {
            return "linear-gradient(to top, red " + (props.baseInfo.hp / props.baseInfo.maxHp) * 100 +  "%, transparent 50%);"
        }
    };
    border: 2px solid black;
    width: 20px;
    height: 90%;
`;
const MainBase = styled.img`
    transform: ${(props) => {
        if (props.className === "enemy") {
            return "scaleX(-1)"
        }else{
            return "scaleX(1)"
        }
    }};
    width: 300px;
`;
const BaseTurret = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    position: absolute;
    width: 80px;
    height: 80px;
    left: 130px;
    bottom: 120px;
`;

export default function Nexus({className, id}){

    const dispatch = useDispatch();
    const baseInfo = useSelector(state => {
        return state.unit.filter(e => e.name === "base" && e.team === className)[0]
    })
    const tower = useSelector(state => {
        return state.tower
    })
    const statusInfo = useSelector(state => {
        return state.status
    })
    const menu = useSelector(state => {
        return state.menu
    })

    return (
        <Container id={id} className={className}>
            <HpState className={className}>
                <HpBar className={className} baseInfo={baseInfo}></HpBar>
                <HpNumber className={className}>{baseInfo.hp}</HpNumber>
            </HpState>
            <MainBase className={className} src={"../image/base/level" + statusInfo.level + ".png"}></MainBase>
            {
                className === "ally" ? <BaseTurret><Turret isBase={true} id={0}></Turret></BaseTurret> : <div></div>
            }
            <Tower></Tower>
        </Container>
    )
}
