import { useDispatch, useSelector } from "react-redux";
import { styled } from "styled-components"

const Container = styled.div`
    display: ${props => {
        return props.on === "turret" ? "flex" : "none"
    }};
    flex-wrap: wrap;
    width: 35%;
    height: 50%;
    gap: 0px 3%;
    margin-left: 2%;

`;
const Title = styled.div`
    color: yellow;
    font-size: 25px;
    width: 100%;
    height: 35%;
`;
const Item = styled.img`
    width: 15%;
    margin-left: ${props => {
        return props.left
    }};
    -webkit-filter: brightness(0.9);
    &:hover{
        -webkit-filter: brightness(1);
    }
`;
export default function ShopTurret({level, on}){

    const dispatch = useDispatch()
    const info = useSelector(state => {
        return state.status
    })

    return(
        <Container on={on}>
            <Title>Menu-Turrets</Title>
            <Item src={"../image/interface/menu/level" + info.level + "/Turret_1.png"} onClick={e => {
                dispatch({type:"menu/changeMenu", menu:"addTurretCancel"})
            }}></Item>
            <Item src={"../image/interface/menu/level" + info.level + "/Turret_2.png"} onClick={ e => {
                dispatch({type:"menu/changeMenu", menu:"addTurretCancel"})
            }}></Item>
            <Item src={"../image/interface/menu/level" + info.level + "/Turret_3.png"} onClick={e => {
                dispatch({type:"menu/changeMenu", menu:"addTurretCancel"})
            }}></Item>
            <Item src={"../image/interface/menu/Prev.png"} left={"20%"} onClick={() => dispatch({type:"menu/changeMenu", menu:"main"})}></Item>
        </Container>
    )
}
