import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled, { keyframes } from "styled-components"
import { ally } from "../../utils/data/dataSet";

const moveAlly = keyframes`
    0%{left: 400px;}
    100%{left: 1000px;}
`;
const moveEnemy = keyframes`
    0%{left: 1000px;}
    100%{left: 400px;}
`;
const Container = styled.div`
    display: flex;
    position: absolute;
    justify-content: center;
    align-content: end;
    flex-wrap: wrap;
    left: 400px;
    bottom: 50px;
    width: 100px;
    height: 130px;
    animation: ${props => props.team === ally ? moveAlly : moveEnemy} 20s 0s 1 linear alternate;
    animation-play-state: ${(props) => props.animation === "걷기" ? "running" : "paused"};
    transform: ${(props) => props.team === ally ? "scaleX(1)" : "scaleX(-1)"};
`;
const UpperBody = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const LowerBody = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const HPBox = styled.div`
    position: relative;
    top: -20px;
    left: 0;
    width: 50px;
    height: 7px;
    background-image: ${(props) => {
            return "linear-gradient(to left, red " + 100 +  "%, transparent 0%);"
        }
    };
    display: none;
`;
const HitBox = styled.div`
    width: 45%;
    height: calc(100% - 30px);
    z-index: 0;
    &:hover {
        ${HPBox} {
            display: block;
        }
    }
`;
const Body = styled.div`
    position: absolute;
    width: 100%;
    height: 100%;
`;

export default function Unit({id, team}){

    const unitInfo = useSelector(state => state.fieldUnit.filter(e => e.id === id)[0])
    const dispatch = useDispatch();

    useEffect(() => {

        // 유닛 만나면 멈추기
        // 상대팀 유닛이면 공격하기
        // 원거리공격범위에 들어오면 원거리공격하기

    }, [unitInfo])

    useEffect(() => {

        // 만약 공격이면 공격속도에 맞게 interval을 생성해서 공격하기

    }, [])

    // 위의 기능들을 재활용 하기 위한 함수 만들기

    return (
        <Container id={id} team={team} animation={unitInfo.lowerBody}>
            <Body>
                <UpperBody src={require("../../assets/images/units/level" + unitInfo.level + "/" + unitInfo.upperBody + "하체.gif")}></UpperBody>
                <LowerBody src={require("../../assets/images/units/level" + unitInfo.level + "/" + unitInfo.lowerBody + "상체.gif")}></LowerBody>
                {/* <audio ref={atteckSound} src={"../sound/" + unitInfo.name + "/" + unitInfo.body + ".mp3"}></audio> */}
            </Body>
            <HitBox><HPBox/></HitBox>
        </Container>
    )
}
