import { useDispatch, useSelector } from "react-redux";
import { styled } from "styled-components";
import { enemy } from "../../utils/data/dataSet";

const Container = styled.div`
    display: flex;
    position: absolute;
    align-items: end;
    flex-direction: column-reverse;
    width: 300px;
    height: 530px;
    bottom: 0;
    left: ${(props) => {
        if (props.className === enemy) {
            return "700px"
        }else{
            return "0px"
        }
    }};
    transform: ${props => props.className === enemy ? "scaleX(-1)" : "scaleX(1)"};
`;
const HpState = styled.div`
    position: absolute;
    display: flex;
    top: 0px;
    left: 70px;
    width: 100px;
    height: 270px;
    gap: 10px;
`;
const HpNumber = styled.div`
    font-size: 20px;
    color: red;
    text-align: center;
`;
const HpBar = styled.div`
    background-image: ${props =>"linear-gradient(to top, red " + 100 +  "%, transparent 50%);"};
    border: 2px solid black;
    width: 20px;
    height: 100%;
    //(props.baseInfo.hp / props.baseInfo.maxHp) * 100
`;
const MainBase = styled.img`
    width: 300px;
`;
const BaseTurret = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    position: absolute;
    width: 80px;
    height: 80px;
    left: 130px;
    bottom: 120px;
    background-color: red;
    z-index: 1;
`;

export default function Nexus({className, id}){

    const dispatch = useDispatch();
    const status = useSelector(state => state.status[className])

    return (
        <Container id={id} className={className}>
            <HpState className={className}>
                <HpBar className={className}></HpBar>
                <HpNumber className={className}>500</HpNumber>
            </HpState>
            <BaseTurret></BaseTurret>
            <MainBase className={className} src={require("../../assets/images/nexus/level" + status.level + ".png")}></MainBase>
        </Container>
    )
}
