import { createSlice } from "@reduxjs/toolkit";
import { ally, enemy } from "../../utils/data/dataSet"

export const status = createSlice({
    name:"status",
    initialState:[{
        level:1,
        team:ally,
        exp:0,
        coin:100,
        tower:[],
        baseTurret:null,
    },{
        level:1,
        team:enemy,
        exp:0,
        coin:100,
        tower:[],
        baseTurret:null,
    }],
    reducers:{
        levelUp:(state, action) => {
            const payload = action.payload
            state[payload.team].level += state[payload.team].level < 5 ? 1 : 0
        },
        addTower:(state, action) => {
            const payload = action.payload
            state[payload.team].tower.push(null)
        }
    }
})
