import { useDispatch } from "react-redux";
import { keyframes, styled } from "styled-components";
import { attacks } from "../../store/slices/attacks";

const animation = (start, finish) => keyframes`
    0%{
        left: ${start.left + "px"};
        bottom: ${start.bottom + "px"};
    }
    100%{
        left: ${finish.left + "px"};
        bottom: ${finish.bottom + "px"};
    }
`;

const Container = styled.div`
    position: absolute;
    width: 50px;
    height: 50px;
    left: 1000px;
    bottom: 0;
    z-index: 5;
    background-color: red;
    animation-name: ${props => animation(props.start, props.finish)};
    animation-duration: 1s;
    animation-iteration-count: 1;
`;

export default function Attack({id, team, target}){

    const dispatch = useDispatch();

    return (
        <Container start={{left:0, bottom:0}} finish={{left:target.location.left, bottom:target.location.bottom}}  onAnimationEnd={e => {
            dispatch(attacks.actions.removeAttack({id:id}))
        }}></Container>
    )
}
