import { useDispatch } from "react-redux";
import { styled } from "styled-components"

const Container = styled.div`
    display: ${props => {
        return props.on === "main" ? "flex" : "none"
    }};
    flex-wrap: wrap;
    width: 35%;
    height: 50%;
    gap: 0px 3%;
    margin-left: 2%;
`;
const Title = styled.div`
    color: yellow;
    font-size: 25px;
    width: 100%;
    height: 35%;
`;
const Item = styled.img`
    width: 15%;
    -webkit-filter: brightness(0.9);
    &:hover{
        -webkit-filter: brightness(1);
    }
`;
export default function ShopMain({on}){


    return(
        <Container on={on}>
            <Title>Menu</Title>
            <Item src={require("../../../assets/images/interfaces/menu/icons/shopMain/unitIcon.png")}></Item>
            <Item src={require("../../../assets/images/interfaces/menu/icons/shopMain/turretIcon.png")}></Item>
            <Item src={require("../../../assets/images/interfaces/menu/icons/shopMain/turretIcon.png")}></Item>
            <Item src={require("../../../assets/images/interfaces/menu/icons/shopMain/turretIcon.png")}></Item>
            <Item src={require("../../../assets/images/interfaces/menu/icons/shopMain/levelUpIcon.png.png")}></Item>
        </Container>
    )
}
