import { keyframes, styled } from "styled-components"

const loading = keyframes`
    0%{width:0%}
    100%{width:100%}
`;

const Load = styled.div`
    margin-top: 2px;
    display: flex;
    flex-wrap: wrap;
    align-content: start;
    width: 43%;
    height: 100%;
    gap: 1%;
    z-index: 1;
`;
const LoadingBar = styled.div`
    border: 2.5px solid black;
    width: 70%;
    height: 8%;
`;
const Progress = styled.div`
    width: 0%;
    height: 100%;
    background-color: lightgray;
    //animation: ${loading} ${props => (props.load.length > 0 ? props.load[0].createTime : 0) + "s"} 0s 1;
`;
const UnitInfo = styled.div`
    color: yellow;
    font-size: 30px;
`;
const LoadWait = styled.div`
    border: 2.5px solid black;
    width: 3.5%;
    height: 9%;
    background-color: ${props => {
        return props.load.length - 1 >= props.index ? "lightgray" : "none"
    }};
`;

export default function Loading(){

    return(
        <Load>
            <LoadingBar>
                <Progress></Progress>
            </LoadingBar>
            {/* <LoadWait></LoadWait>
            <LoadWait></LoadWait>
            <LoadWait></LoadWait>
            <LoadWait></LoadWait>
            <LoadWait></LoadWait> */}
            <UnitInfo>Club Man...</UnitInfo>
        </Load>
    )
}
