import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled, { keyframes } from "styled-components"
import { ally } from "../../utils/data/dataSet";

const moveAlly = keyframes`
    0%{left: 400px;}
    100%{left: 1000px;}
`;
const moveEnemy = keyframes`
    0%{left: 1000px;}
    100%{left: 400px;}
`;
const Container = styled.div`
    display: flex;
    position: absolute;
    justify-content: center;
    align-content: end;
    flex-wrap: wrap;
    left: 400px;
    bottom: 50px;
    width: 100px;
    height: 130px;
    animation: ${props => props.className === ally ? moveAlly : moveEnemy} 20s 0s 1 linear alternate;
    /* animation-play-state: ${(props) => props.animation || "paused"}; */

`;
const UpperBody = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const LowerBody = styled.img`
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
`;
const HPBox = styled.div`
    position: relative;
    top: -20px;
    left: 0;
    width: 50px;
    height: 7px;
    background-image: ${(props) => {
            return "linear-gradient(to left, red " + 100 +  "%, transparent 0%);"
        }
    };
    display: none;
`;
const HitBox = styled.div`
    width: 45%;
    height: calc(100% - 30px);
    z-index: 0;
    &:hover {
        ${HPBox} {
            display: block;
        }
    }
`;
const Body = styled.div`
    position: absolute;
    width: 100%;
    height: 100%;
`;

