import { useSelector } from "react-redux";
import { styled } from "styled-components";
import targetImg from "../../assets/images/turrets//target.png"

const Container = styled.div`
    display: flex;
    position: absolute;
    align-items: center;
    justify-content: center;
    width: 55px;
    height: 55px;
    left: ${props => props.location.left + "px"};
    right: ${props => props.location.bottom + "px"};
`;
const TurretImage = styled.img`
    position: absolute;
    width: 140px;
    height: 140px;
    transform: rotate( ${props => props.deg + "deg"});
`;
const TargetImage = styled.img`
    position: absolute;
    width: 55px;
    height: 55px;
    z-index: 10;
    &:hover{
        background-color: rgba(240, 240, 240, 0.5);
    }
`;

export default function Turret({location}){

    return (
        <Container location={location}>
            <TurretImage ></TurretImage>
            <TargetImage src={targetImg}></TargetImage>
        </Container>
    )
}
