import { useDispatch, useSelector } from "react-redux";
import { keyframes, styled } from "styled-components";
import { attacks } from "../../store/slices/attacks";
import { useEffect } from "react";

const animation = (start, finish) => keyframes`
    0%{
        left: ${start.left + "px"};
        top: ${start.top + "px"};
    }
    100%{
        left: ${finish.left + "px"};
        top: ${finish.top + "px"};
    }
`;

const Container = styled.div`
    position: absolute;
    width: 50px;
    height: 50px;
    left: 1000px;
    bottom: 0;
    z-index: 4;
    background-color: red;
    animation-name: ${props => animation(props.start, props.finish)};
    animation-duration: 1s;
    animation-iteration-count: 1;
`;

export default function Attack({id, team}){

    const dispatch = useDispatch();
    const attack = useSelector(state => state.attacks[state.attacks.findIndex(e => e.id === id)])
    useEffect(() => {
        console.log(attack);
    }, [])

    return (
        <>
          {Object.keys(attack.target).length > 0 ? <Container
              start={{ left: attack.location.left, top: attack.location.top }}
              finish={{ left: attack.target.location.left, top: attack.target.location.top }}
              onAnimationEnd={e => dispatch(attacks.actions.removeAttack({ id: id }))}
            ></Container> : <></>}
        </>
      );
}
