import { createSlice } from "@reduxjs/toolkit";
import { ally, enemy } from "../../utils/data/dataSet"

export const status = createSlice({
    name:"status",
    initialState:[{
        level:1,
        team:ally,
        exp:0,
        coin:100,
        tower:[],
        baseTurret:null,
        menu:"main"
    },{
        level:1,
        team:enemy,
        exp:0,
        coin:100,
        tower:[],
        baseTurret:null,
    }],
    reducers:{
        levelUp:(state, action) => {
            const payload = action.payload
            if(state[payload.team].level < 5) state[payload.team].level++
        },
        addTower:(state, action) => {
            const payload = action.payload
            if(state[payload.team].tower.length < 3) state[payload.team].tower.push(null)
        },
        setMenu:(state, action) => {
            state[0].menu = action.payload.menu
        }
    }
})
