import { styled } from "styled-components";
import menuImg from "../../assets/images/interfaces/menu/menuBar.png"
import Wallet from "./wallet/Wallet";
import Loading from "./production/Loading";
import ShopTurret from "./shop/ShopTurret";
import ShopMain from "./shop/ShopMain";
import ShopUnit from "./shop/ShopUnit";
import { useSelector } from "react-redux";

const Container = styled.div`
    position: absolute;
    width: 100%;
`;
const MenuBar = styled.div`
    display: flex;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    gap: 5px 1%;
`;

export default function Menu(){

    const menu = useSelector(state => state.status[0].menu)

    return(
        <Container>
            <img src={menuImg} width={"100%"}></img>
            <MenuBar>
                <Wallet></Wallet>
                <Loading></Loading>
                <ShopTurret menu={menu}></ShopTurret>
                <ShopUnit menu={menu}></ShopUnit>
                <ShopMain menu={menu}></ShopMain>
                {/* <Cancel></Cancel> */}
            </MenuBar>
        </Container>
    )
}
