import { useDispatch } from "react-redux";
import { styled } from "styled-components"
import { status } from "../../../store/slices/status";

const Container = styled.div`
    display: ${props => props.menu === "add" || props.menu === "cancel" ? "flex" : "none"};
    flex-wrap: wrap;
    width: 35%;
    height: 50%;
    gap: 0px 3%;
    margin-left: 2%;
`;
const Title = styled.div`
    color: yellow;
    font-size: 25px;
    width: 100%;
    height: 35%;
`;
const CancelButton = styled.button`
    color: red;
    background-color: lightgray;
    border: 2px solid black;
    font-size: 30px;
    width: 65%;
    height: 75%;
    margin-left: 14%;
    &:hover{
        background-color: rgb(240, 240, 240)
    }
`;
export default function ShopCancel({on}){

    const dispatch = useDispatch();

    return(
        <Container on={on}>
            <Title>Menu</Title>
            <CancelButton onClick={ e => dispatch(status.actions.setMenu({menu:"main"}))}>CANCEL</CancelButton>
        </Container>
    )
}
